					$sql = 'SELECT id, slug, reward, title, iconName, taskType FROM tasks WHERE taskType = "Daily" ORDER BY id DESC;';
					$result = $conn->query($sql);
					if ($result->num_rows > 0) {
						while($row = $result->fetch_assoc()) {
							echo 
							'<a style="position: relative;" class="important_link_item" target="_blank" href="'. $row["slug"] .'">
								<div style="display: flex; align-item: center;">
									<div style="min-width: 60px; border-right: 1px dashed #c1c1c1; margin-right: 10px;">
										<p class="important_link_heading">'. $icons[$row["iconName"]] .'</p>
										<p class="important_linkitem" style="color: #000">'. $row["reward"] .'</p>
									</div>
									<div style="overflow: hidden;">
										<p class="important_link_heading">'.  $row["title"] .'</p>
										<p class="important_linkitem">'. $row["slug"] .'</p>
									</div>
								</div>




								<form method="post" action="forms/delete_the_task.php">
									<input type="hidden" name="idToDelete" value="'. $row["id"] .'">
